    public const string VerificationEmailTemplate = "<!DOCTYPE html>\n<html>\n<head>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            color: #333;\n            line-height: 1.6;\n        }\n        .container {\n            width: 100%;\n            max-width: 600px;\n            margin: 0 auto;\n            padding: 20px;\n            border: 1px solid #e1e1e1;\n            border-radius: 8px;\n            background-color: #f9f9f9;\n        }\n        h2 {\n            color: #4CAF50;\n        }\n        .button {\n            display: inline-block;\n            padding: 10px 20px;\n            color: #fff;\n            background-color: #4CAF50;\n            text-decoration: none;\n            border-radius: 5px;\n            margin-top: 20px;\n        }\n        .footer {\n            margin-top: 20px;\n            font-size: 0.9em;\n            color: #777;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <h2>Hello, {{UserName}}!</h2>\n        <p>Thank you for signing up! To complete your registration and activate your account, please verify your email by clicking the link below:</p>\n        <p><a href=\"{{VerificationLink}}\" class=\"button\">Verify Your Account</a></p>\n        <p>If the button above doesn’t work, copy and paste the following link into your browser:</p>\n        <p>{{VerificationLink}}</p>\n        <div class=\"footer\">\n            <p>If you did not sign up for this account, please ignore this email.</p>\n            <p>Thank you,<br>Nosh Now</p>\n        </div>\n    </div>\n</body>\n</html>";
